import React, { useState } from 'react'

function TodoForm(props) {
    const [input, setInput] = useState('');
    const [todoId, setTodoId] = useState(0);

  const handleChange = e => {
    setInput(e.target.value);
  }

  const handleSubmit = e => {
    e.preventDefault();

    console.log(props.onSubmit)

    // props.onSubmit({
    //   id: setTodoId(todoId+1),
    //   text: input
    // });
    
    setInput('');
  }

  return (
    <section className='tasks'>
      <div className='main-block__info'>
        <h1 className='main-block__title'>Welcome to React TODO App!</h1>
        <p>Enter a task below for adding it to list of todo's</p>
      </div>
      <form className='task-form' onSubmit={handleSubmit}>
        <input 
            type='text' 
            placeholder='Your task' 
            value={input} 
            className='task-input' 
            onChange={handleChange}
        />
        <button className='task-input__add-button'>Add to list</button>
      </form>
    </section>
    
  )
}

export default TodoForm
