import React, { useState } from 'react'

function Kosiposha () {
  const [message, setMessage] = useState('');

  return (
    <div className='main-page-block__task-input'>
      <input type='text' placeholder='Your task' className='main-page-block__input'></input>
      <button type='button' className='main-page-block__add-button' onClick={() => {setMessage(message = document.querySelector('main-page-block__input').textContent)}}>Add to list</button>
    </div>
  )
}

class App extends React.Component {
    render () {
      return (
        <div className='main-page-block'>
          <div className='main-page-block__info'>
            <h1 className='main-page-block__title'>Welcome to React TODO App!</h1>
            <p>Enter a task below for adding it to list of todo's</p>
          </div>
          <Kosiposha />
        </div>
      );
    }
  }

export default App
