import React, { useState } from 'react'

function Kosiposha () {
  let [message, setMessage] = useState('');

  return (
    <section className='tasks'>
      <p className='last-todo'>Last todo: { message }</p>
      <div className='task-form'>
        <input type='text' placeholder='Your task' className='task-input'></input>
        <button type='button' className='task-input__add-button' onClick={() => {setMessage(message = document.querySelector('.task-input').value)}}>Add to list</button>
    </div>
    </section>
    
  )
}

class App extends React.Component {
    render () {
      return (
        <div className='main-block'>
          <div className='main-block__info'>
            <h1 className='main-block__title'>Welcome to React TODO App!</h1>
            <p>Enter a task below for adding it to list of todo's</p>
          </div>
          <Kosiposha />
        </div>
      );
    }
  }

export default App
