import React, { useState } from 'react'

function Kosiposha () {
  let [message, setMessage] = useState('');

  const handleSubmit = (evt) => {
    evt.preventDefault();
    
    setMessage((document.querySelector('.task-input').value))
  }

  return (
    <section className='tasks'>
      <p className='last-todo'>Last todo: { message }</p>
      <form className='task-form' onSubmit={handleSubmit}>
        <input type='text' placeholder='Your task' className='task-input'/>
        <button className='task-input__add-button'>Add to list</button>
      </form>
    </section>
    
  )
}

class App extends React.Component {
    render () {
      return (
        <div className='main-block'>
          <div className='main-block__info'>
            <h1 className='main-block__title'>Welcome to React TODO App!</h1>
            <p>Enter a task below for adding it to list of todo's</p>
          </div>
          <Kosiposha />
        </div>
      );
    }
  }

export default TodoForm
