import React from "react";
import { AiOutlineCloseCircle, AiOutlineEdit } from 'react-icons/ai';
import '../style.css'
import { MdDone } from 'react-icons/md'
import { BiUndo } from 'react-icons/bi'

function Todo(props) {
    return (
        <section class='todo-row'>
            <div className={props.todo.isComplete ? 'todo complete' : 'todo'}>
                <span className='todo-text'>{props.todo.text}</span>
                <div className='todo-buttons'>
                    <AiOutlineCloseCircle 
                        className='todo__delete-button'
                        onClick={props.onDeleteClick}
                    />
                    <AiOutlineEdit 
                        className={props.todo.isComplete ? 'todo__edit-button disable' : 'todo__edit-button'} onClick={props.onEditClick}
                    />
                    {props.todo.isComplete ? 
                    <BiUndo className='todo__uncomplete-button' onClick={props.onChangeStateClick}/> : <MdDone className='todo__complete-button' onClick={props.onChangeStateClick}/>}
                </div>
            </div>
            <input type='text' className={style.task-input}/>
        </section>
    )
}

export default Todo
